import {publicRequest} from '../requestMethods'
import {
    allProductsRequest,
    allProductsSuccess, 
    allProductsFailure
} from '../Slice/product/productsSlice'

import {
    productDetailsRequest,
    productDetailsSuccess, 
    productDetailsFailure
} from '../Slice/product/productSlice'


//get all products
export const getProduct = (keyword = "", currentPage = 1, price = [0, 10000000], category) => async (dispatch) => {
    try{
        dispatch(allProductsRequest);

        let link = `/products?keyword=${keyword}&page=${currentPage}&price[gte]=${price[0]}&price[lte]=${price[1]}`;

        // if (category) {
        //     link = `/api/v1/products?keyword=${keyword}&page=${currentPage}&price[gte]=${price[0]}&price[lte]=${price[1]}&category=${category}`;
        // }

        const { data } = await publicRequest.get(link);
        
        dispatch(allProductsSuccess(data.products));
    }catch(err){
        dispatch(allProductsFailure(err.response.data.message));
    }
}


// get  product detail
export const getProductDetail = (id) => async(dispatch) =>{
    try{
        dispatch(productDetailsRequest());

        const { data } = await publicRequest.get(
            `http://localhost:4000/api/v1/product/628b14dee6ec4f6044b888af`
        );
        
        dispatch(productDetailsSuccess(data.product));
    }catch(err){
        dispatch(productDetailsFailure(err.response.data.message));
    }   
}

