import { createSlice } from "@reduxjs/toolkit";

export default productsSlide = createSlice({
    name:"products",
    initialState:{
        products:[],
        isLoading: false,
        // isAuthenticated: false,
        error: "",
    },
    reducers:{
        allProductRequest:(state)=>{
            state.isLoading = true
        },
        allProductSuccess:(state, action)=>{
            state.isLoading = false
            state.products = action.payload
        },
        allProductFailure:(state,action)=>{ 
            state.isLoading = false
            state.error = action.payload
        },

        productDetailRequest: (state)=>{
            state.isLoading = true
        },
        productDetailSuccess: (state, action)=>{
            state.isLoading = false
            state.products = action.payload
        },
        productDetailFailure: (state, action)=>{
            state.isLoading =  false
            state.error = action.payload
        },

        clearError:(state) =>{
            state.error = ""
        }
    },
    extraReducers:{
        
    }
})

export const {allProductRequest, allProductSuccess, allProductFailure,
    productDetailRequest, productDetailSuccess, productDetailFailure,
     clearError} = productsSlide.actions;
// export default productsSlide.reducer
