import { createSlice } from "@reduxjs/toolkit";

export const productsSlide = createSlice({
    name:"products",
    initialState:{
        products:[],
        isLoading: false,
        // isAuthenticated: false,
        error: "",
    },
    reducers:{
        allProductRequest:(state)=>{
            state.isLoading = true
        },
        allProductSuccess:(state, action)=>{
            state.isLoading = false
            state.products = action.payload
        },
        allProductFailure:(state,action)=>{ 
            state.isLoading = false
            state.error = action.payload
        },

        productDetailRequest: (state)=>{
            state.isLoading = true
        },
        productDetailSuccess: (state)=>{
            state.isLoading = false
            state.products = action.payload
        },
        productDetailFailure: (state, action)=>{
            loading =  false
            error = action.payload
        },

        clearError:(state) =>{
            state.error = ""
        }
    },
    extraReducers:{
        
    }
})

export const {allProductRequest,allProductSuccess, allProductFailure, clearError} = productsSlide.actions;
export default productsSlide.reducer
