import { createSlice } from "@reduxjs/toolkit";

export const productsSlide = createSlice({
    name:"products",
    initialState:{
        products:[],
        isLoading: false,
        error: "",
    },
    reducers:{
        allProductsRequest:(state)=>{
            state.isLoading = true
        },
        allProductsSuccess:(state, action)=>{
            state.isLoading = false
            state.products = action.payload
        },
        allProductsFailure:(state,action)=>{ 
            state.isLoading = false
            state.error = action.payload
        },
        clearError:(state) =>{
            state.error = ""
        }
    },
})

export const {allProductsRequest,allProductsSuccess, allProductsFailure, clearError} =  productsSlide.actions
