import { createSlice } from "@reduxjs/toolkit";

export const productsSlide = createSlice({
    name:"products",
    initialState:{
        products:[],
        isLoading: false,
        error: "",
    },
    reducers:{
        allProductRequest:(state)=>{
            state.isLoading = true
        },
        allProductSuccess:(state, action)=>{
            state.isLoading = false
            state.products = action.payload
        },
        allProductFailure:(state,action)=>{ 
            state.isLoading = false
            state.error = action.payload
        },

        clearError:(state) =>{
            state.error = ""
        }
    },
})


export const {allProductRequest,allProductSuccess, allProductFailure, clearError} =  productsSlide.actions
export default productsSlide.actions
