import React, { Fragment, useState } from 'react'
import styled from '@emotion/styled';

import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';

import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';


import { Backdrop, SpeedDial, SpeedDialIcon, SpeedDialAction } from '@mui/material';




const StyledSpeedDial = styled(SpeedDial)`
  position: fixed;
  top:8px;
  right:30px;
  z-index:11;
`



const Account = ({currentUser}) => {

  const history = useNavigate()
  const [open, setOpen] = useState(false)
  

  const actions = [
    { icon: <ShoppingCartOutlinedIcon />, name: 'Cart', func: cart },
    { icon: <AccountCircleIcon />, name: 'Profile', func: account},
    { icon: <ExitToAppIcon />, name: 'Logout', func: logoutUser },
  ];
  
  
  function cart(){
    history("/cart");
  }

  function account(){
    history.push("/account");
  }
  function logoutUser(){

  }

  

  return (
      <Fragment>
        <Backdrop open={open} style={{zIndex: "10"}}/>
        <StyledSpeedDial
          ariaLabel='SpeedDial controlled open exampl'
          onClose={()=>setOpen(false)}
          onOpen={()=>setOpen(true)}
          open={open}
          icon={<SpeedDialIcon />}
          direction="down"
        >
          {actions.map((action) => (
            <SpeedDialAction
              key={action.name}
              icon={action.icon}
              tooltipTitle={action.name}
              onClick={action.func}
              // tooltipOpen={window.innerWidth <= 600 ? true : false}
            />
          ))}
        </StyledSpeedDial>
      </Fragment>
  );
}

export default Account
